document.addEventListener('DOMContentLoaded', () => {
  const e = document.querySelector('.header'),
    r = document.querySelector('.header__container'),
    t = document.querySelector('.header__logout');
  window.onscroll = () => {
    window.pageYOffset > 30
      ? (e.classList.add('header__scroll'),
        r.classList.add('header__container__scrool'),
        t.classList.add('header__logout__scroll'))
      : (e.classList.remove('header__scroll'),
        r.classList.remove('header__container__scrool'),
        t.classList.remove('header__logout__scroll'));
  };
  const o = document.querySelector('.header__burger'),
    c = document.querySelector('.burger__menu'),
    s = document.querySelector('.dark-wrapper'),
    a = () => {
      c.classList.toggle('burger__menu-active'), o.classList.toggle('active'), s.classList.toggle('active');
    };
  o.addEventListener('click', a),
    document.addEventListener('click', (e) => {
      e.target.classList.contains('dark-wrapper') && a();
    });
});
