document.addEventListener('DOMContentLoaded', () => {
  if (document.querySelector('.popup')) {
    const e = document.querySelector('.popup'),
      t = document.querySelector('.button__order__auto'),
      o = document.querySelector('.button__order'),
      c = document.querySelector('.popup__close'),
      n = document.querySelector('.dark__container'),
      d = document.body,
      r = () => {
        e.classList.toggle('popup__hidden'),
          n.classList.toggle('dark__container__noactive'),
          d.classList.toggle('body__hidden');
      },
      u = (e) => e.target.classList.contains('popup');
    t.addEventListener('click', r),
      o.addEventListener('click', r),
      c.addEventListener('click', r),
      e.addEventListener('click', () => {
        u() && r();
      });
  }
});
