document.addEventListener("DOMContentLoaded",(()=>{if(document.querySelector(".services__collapsible")){const e=document.querySelectorAll(".services__collapsible"),l=document.querySelectorAll(".fa-plus");for(let s=0;s<e.length;s+=1)e[s].addEventListener("click",(function(){const e=this.nextElementSibling;e.style.maxHeight?(e.style.maxHeight=null,l[s].classList.remove("fa-minus"),l[s].classList.add("fa-plus")):(e.style.maxHeight=`${e.scrollHeight}px`,l[s].classList.remove("fa-plus"),l[s].classList.add("fa-minus"))}))}}));