const SIDEBAR = document.querySelector('.sidebar__list');
const SIDEBAR_ITEMS = document.querySelectorAll('.sidebar__item');
const MODEL_CARS = document.querySelectorAll('.model__cars-js');
const ERROR = document.querySelector('.error');
const MODELS_TITLE = document.querySelectorAll('.model__title');
const showToggleCars = (e) => {
  let l = !1;
  if (e == 'all') {
    for (let e = 0; e < MODEL_CARS.length; e++) {
      (MODEL_CARS[e].style.display = 'flex'),
        location.href.includes('admin') || (MODELS_TITLE[e].style.display = 'block'),
        (l = !0);
    }
  } else {
    for (let t = 0; t < MODEL_CARS.length; t++) {
      MODEL_CARS[t].getAttribute('model') != e
        ? ((MODEL_CARS[t].style.display = 'none'),
          location.href.includes('admin') || (MODELS_TITLE[t].style.display = 'none'))
        : ((MODEL_CARS[t].style.display = 'flex'),
          location.href.includes('admin') || (MODELS_TITLE[t].style.display = 'block'),
          (l = !0));
    }
  }
  showToggleError(l);
};
const showToggleError = (e) => {
  ERROR.innerHTML = !1 === e ? 'Авто данной модели нет в наличии!' : '';
};
const getModel = (e) => SIDEBAR_ITEMS[e].getAttribute('model');
SIDEBAR.addEventListener('click', (e) => {
  const l = e.target.id;
  for (let e = 0; e < SIDEBAR_ITEMS.length; e++) SIDEBAR_ITEMS[e].classList.remove('sidebar__item-active');
  SIDEBAR_ITEMS[l].classList.add('sidebar__item-active'), (model = getModel(l)), showToggleCars(model);
});
